function CityList(props) {
  // const cities = props.cities;
  const { cities } = props;

  return (
    <div>
      <h1>CityList</h1>

      <ul>
        {cities.map((item, index) => {
          console.log(index);

          return 
            <li class ="city" key={index} >
              <button onClick={this.onHandleClick}>Get Google Maps Directions</button>

              {item}
            </li>;
        })}
      </ul>
    </div>
  );
}
var onHandleClick = () => {
  var location = this.state.currentLocation;
  location.then(function(location){
    var link = `google.com/maps/dir/?api=1&origin=${location}&destination=50.927044,-1.299964&travelmode=driving`      
    //window.location.href = link;
    window.location.assign(link);
  })
}

export default CityList;
