class NavToggle extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      isSidebar: false
    };
    this.handleClick = this.handleClick.bind(this);

  }
  handleClick(){
    this.setState( state => ({
      isSidebar: !state.isSidebar
    }));    
  }
  render(){

const button = 
      <button onClick={this.handleClick}>{this.state.isSidebar ? "Click to change state to False" : "Click to change state to True"} </button>


    return(
      <div>
        {this.state.isSidebar ? <h1>Wow! you change the State to  True</h1> : <h1>Again!! now U change state to False</h1>}
        {button}
      </div>
    );
  }
}

function find(props) {
  var link = 'https://www.google.com/maps/search/?api=1&query='+props;
  window.open(link);
  console.log(this.props.className)
}
function CityList(props) {
  const { cities } = props;
  return (

    <div>
      <h1>CityList</h1>
      <ul>
        {cities.map((item, _) => {          
          return (
              <button className = "find" onClick={()=>find(item)}>
              {item}
              </button>
            );
        })}
      </ul>
    </div>
  );
}


export default CityList;
