import logo from './logo.svg';
import './App.css';
import Home from "./pages/Home";
import Cities from "./pages/Cities";
import About from "./pages/About";
import Header from "./components/Header";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
function App() {
  const city = "Daejeon";
  return (
    <Router>
      <div className="App">
       <Header where = {city} />
       <ul className ="menu">
       <li>
         <Link to ="/">Home</Link>
       </li>
       <li>
        <Link to ="/about">About</Link>
       </li>
       <li>
        <Link to ="/cities">Cities</Link>
       </li>
       </ul>
       <Switch>
        <Route path = "/cities">
          <Cities/>
          </Route> 
          <Route path = "/about">
          <About/>
          </Route>
          <Route path = "/">
          <Home/>
          </Route>
       </Switch>
      </div>
    </Router>

  );
}

export default App;
